import React, {Component} from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import './App.css';

//on constructor, super must be called this constructor shows a state of the app
//each state has what is in the search field and the robots being displayed
class App extends Component{
    constructor(){
        super()
        this.state = {
            robots: [],
            searchField: ''
        }
    }
    
//this is what is updating the app. changes the robots that are called? idk really
    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
        .then(response =>{response.json()})
        .then(users =>{this.setState({robots: users})});
    }

//this updates searchField to show whatever user is typing
    onSearchChange = (event) =>{
        this.setState({ searchField: event.target.value})
    }

//this renders code to front end of app
//filteredRobots filters robots that match with input in searchField
    render(){
        const filteredRobots = this.state.robots.filter(robot =>{ 
            return robot.name.toLowerCase().includes(this.state.searchField.toLowerCase());
        })
        return(
            <div className = 'tc'>
                <h1 className = 'f1'>RoboFriends</h1>
                <SearchBox searchChange={this.onSearchChange}/>
                <CardList robots={filteredRobots}/>
            </div>
        );
    }
}

export default App;
